package negocio;import controlador.ControladorProduccion;import dao.AlmacenamientoDAO;import dao.LoteDAO;import dao.PrendaVentaDAO;import dto.LoteDTO;import entities.LoteEntity;public class Lote {	private int idLote;	private Prenda prenda;	private OrdenDeProduccion ordenDeProduccion;	private int proceso;	private String estado;	private float costoProd;	private Almacenamiento ubicacion;	private int cantidadProducida;	private boolean activo;	public Lote() {	}	public Lote(int idLote, Prenda prenda, OrdenDeProduccion ordenDeProduccion,			int proceso, String estado, float costoProd,			Almacenamiento ubicacion, int cantidadProducida,			int cantidadRestante, boolean activo) {		super();		this.idLote = idLote;		this.prenda = prenda;		this.ordenDeProduccion = ordenDeProduccion;		this.proceso = proceso;		this.estado = estado;		this.costoProd = costoProd;		this.ubicacion = ubicacion;		this.cantidadProducida = cantidadProducida;		this.activo = activo;	}		public Lote(OrdenDeProduccion ordenDeProduccion, Prenda prenda,			int proceso, String estado, float costoProd,			Almacenamiento ubicacion, int cantidadProducida,			int cantidadRestante, boolean activo) {		super();		this.ordenDeProduccion = ordenDeProduccion;		this.prenda=prenda;		this.proceso = proceso;		this.estado = estado;		this.costoProd = costoProd;		this.ubicacion = ubicacion;		this.cantidadProducida = cantidadProducida;		this.activo = activo;	}	public int getIdLote() {		return idLote;	}	public void setIdLote(int idLote) {		this.idLote = idLote;	}	public Prenda getPrenda() {		return prenda;	}	public void setPrenda(Prenda prenda) {		this.prenda = prenda;	}	public OrdenDeProduccion getOrdenDeProduccion() {		return ordenDeProduccion;	}	public void setOrdenDeProduccion(OrdenDeProduccion ordenDeProduccion) {		this.ordenDeProduccion = ordenDeProduccion;	}	public int getProceso() {		return proceso;	}	public void setProceso(int proceso) {		this.proceso = proceso;	}	public String getEstado() {		return estado;	}	public void setEstado(String estado) {		this.estado = estado;	}	public float getCostoProd() {		return costoProd;	}	public void setCostoProd(float costoProd) {		this.costoProd = costoProd;	}	public Almacenamiento getUbicacion() {		return ubicacion;	}	public void setUbicacion(Almacenamiento ubicacion) {		this.ubicacion = ubicacion;	}	public int getCantidadProducida() {		return cantidadProducida;	}	public void setCantidadProducida(int cantidadProducida) {		this.cantidadProducida = cantidadProducida;	}	public boolean isActivo() {		return activo;	}	public void setActivo(boolean activo) {		this.activo = activo;	}	public LoteDTO toDTO() {		LoteDTO aux = new LoteDTO();		aux.setActivo(this.isActivo());		aux.setCantidadProducida(this.getCantidadProducida());		aux.setCostoProd(this.getCostoProd());		aux.setEstado(this.getEstado());		aux.setIdLote(this.getIdLote());		if(this.getOrdenDeProduccion()!=null){			aux.setOrdenDeProduccion(this.getOrdenDeProduccion().toDTO());		}		if(this.getPrenda()!=null){		aux.setPrenda(this.getPrenda().toDTO());		}		aux.setProceso(this.getProceso());		if(this.getUbicacion()!=null){			aux.setUbicacion(this.getUbicacion().ToDTO());		}		return aux;	}		}